package Pattern;
import java.util.*;
public class Series1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int n=sc.nextInt();
		int temp=1;
		for(int i=1;i<=n;i++) {
			System.out.print(temp +" ");
			temp+=2;
			
		}

	}

}
